import { Component, Input, OnInit } from '@angular/core';
import { IngresoServicio } from '../ingreso/ingreso.service';
import { EgresoServicio } from '../egreso/egreso.service';
import { ToastrService } from 'ngx-toastr';


/*app-formulario
  Componente formulario
  Permite agregar ingresos o egresos con un formulario.
*/
@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.css']
})
export class FormularioComponent implements OnInit {

  //Variable que define si la operación es un ingreso o egreso,
  //por defecto, la operación es de ingreso.
  tipo:string="ingresoOperacion";

  //Variables que corresponden a los campos de ingreso o egreso.
  descripcionInput:string;
  valorInput:number;

  /*Variable que viene desde componente padre
  app.component que inyectamos
  gracias al "@Input()"*/
  @Input() presupuestoTotal:number;

  //Se inyecta una dependencia de los servicios de ingreso y egreso.
  constructor(private ingresoServicio: IngresoServicio,
              private egresoServicio: EgresoServicio,
              private toastr: ToastrService) { }

  ngOnInit(): void {
  }

  //Asigna el valor del tipo de operacion cuando se cambia el valor del select del html.
  tipoDeOperacion(evento):void{
    this.tipo = evento.target.value;
  }

  //Dependiendo del tipo de operación, se hace la inserción al arreglo de ingresos o
  //egresos según corresponda.
  agregarValor(){
    if(this.tipo === "ingresoOperacion"){
      this.ingresoServicio.agregarIngreso(this.descripcionInput, this.valorInput);
      this.notificarInsersion("Ingreso");
    }else if (this.tipo === "egresoOperacion"){
    

      this.egresoServicio.agregarEgreso(this.descripcionInput, this.valorInput);
      this.notificarInsersion("Egreso");
    }

  }

  //Función para mostrar la notificación de que se hizo una inserción correctamente
  //con el uso de la librería ngx-toastr
  notificarInsersion(tipo: string) {
    this.toastr.success(this.descripcionInput + ' de $' + this.valorInput, tipo + " realizado con exito");
  }

}
<script src="/example-path-to/jquery-2.1.4.js"></script>
<script src="/example-path-to/toastr.js"></script>
<link href="/example-path-to/toastr.css" rel="stylesheet" />

<form (ngSubmit)="f.form.valid && agregarValor()" #f="ngForm">
  <div class="agregar">
    <div class="agregar_contenedor">

      <select [ngClass]="{redfocus:tipo ==='egresoOperacion'}" (change)="tipoDeOperacion($event)" class="agregar_tipo">
        <option value="ingresoOperacion" selected>Ingreso</option>
        <option value="egresoOperacion">Egreso</option>
      </select>
      <!--ngModel permite el two-way binding para que guarde el
          contenido del input como valor de la variable
          required hace que no se pueda hacer subdmit si este input
          está vacío-->
      <input [ngClass]="{redfocus:tipo ==='egresoOperacion'}" type="text" class="agregar_descripcion"
        name="descripcionInput" [(ngModel)]="descripcionInput" placeholder="Agregar Descripción" required>

      <!--ngModel permite el two-way binding para que guarde el
          contenido del input como valor de la variable-->
      <input [ngClass]="{redfocus:tipo ==='egresoOperacion'}" type="number" class="agregar_valor" name="valorInput"
        [(ngModel)]="valorInput" placeholder="Valor" required>
      <!--El type="submit" hace que reconozca el enter
              cuando se capturen los datos-->
      <button type="submit" class="agregar_btn">
        <i [ngClass]="{red:tipo ==='egresoOperacion'}" class="ion-ios-checkmark-outline">
        </i>
      </button>
    </div>
  </div>
</form>
