import { Component, Input, OnInit } from '@angular/core';

/*app-cabecero
  Componente cabecero, parte superior de la página que contiene
  el presupuesto disponible, el total de ingresos, egresos y el
  porcentaje de egresos con respecto a los ingresos
*/
@Component({
  selector: 'app-cabecero',
  templateUrl: './cabecero.component.html',
  styleUrls: ['./cabecero.component.css']
})


export class CabeceroComponent implements OnInit {

  /*Variables que vienen desde componente padre
  app.component que inyectamos
  gracias al "@Input()"*/
  @Input() presupuestoTotal:number;
  @Input() ingresoTotal:number;
  @Input() egresoTotal:number;
  @Input() porcentajeTotal:number;

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="cabecero">
  <div class="presupuesto">
    <div class="presupuesto_titulo">
      Presupuesto Disponible:
    </div>

    <div class="presupuesto_valor"> {{presupuestoTotal | currency:'MNX':'$': '1.0-2'}}</div>

    <div class="presupuesto_ingreso limpiarEstilos">
      <div class="presupuesto_ingreso--texto">Ingresos</div>
      <div class="right">
        <div class="presupuesto_ingreso--valor">{{ingresoTotal | currency:'MNX':'$': '1.0-2'}}</div>
        <div class="presupuesto_ingreso--porcentaje">&nbsp;</div>
      </div>
    </div>

    <div class="presupuesto_egreso limpiarEstilos">
      <div class="presupuesto_egreso--text">Egresos</div>
      <div class="right limpiarEstilos">
        <div class="presupuesto_egreso--valor">{{egresoTotal | currency:'MNX':'$': '1.0-2'}}</div>
        <div class="presupuesto_egreso--porcentaje">{{porcentajeTotal | percent}}</div>
      </div>
    </div>
  </div>
</div>
